import React, {useState} from 'react'
import {Link} from "react-router-dom";

import "../css/breadcrumb.css"



export default function Breadcrumb({content, location}) {

    const [isActive, setIsActive] = useState(true)

    const HandleClick = (e) =>
    {
        const anchor = document.querySelector('a');
        if(anchor.classList.contains("link-deactivated"))
        {
           setIsActive(false);
        }
        else
        {
            anchor.classList.add("link-deactivated");
        }
    }
    return (
        <div className='flex'>
            <Link  to={isActive ? location + "/" + content: null} onClick={event => HandleClick(event)}>{content}</Link>
        </div>
    )
  
}
