import React, {useEffect, useState} from 'react'
import {useLocation} from "react-router-dom";
import Breadcrumb from './breadcrumb';

export default function GetContents() {
    let location = useLocation();
    const [fileContents, setFileContents] = useState({contents:[]});
    useEffect(()=>
    {
        console.log(location);
        fetch("/api" + location.pathname).then(response => response.json()).then(data => {
            console.log(data.contents);
            setFileContents({contents: data.contents});
            console.log(fileContents);
        })        
    }, [location])
    return (
    <div>{fileContents.contents.map(file => {
        <Breadcrumb content = {file} />
    })}</div>
  )
}
