import React, {useEffect, useState} from 'react'
import {useLocation} from "react-router-dom";
import Breadcrumb from './breadcrumb';

export default function GetContents() {
    let location = useLocation();
    let isFile = true;
    const [fileContents, setFileContents] = useState([]);
    const [fileDescription, setFileDescription] = useState("");
    useEffect(()=>
    {
        fetch("/api" + location.pathname).then(response => response.json()).then(data => {
            if(data.contents === undefined)
            {
                setFileContents([]);
            }
            else
            {
                setFileContents(data.contents);
                if(data.currentElement === 'file')
                {
                    return data.currentElement;
                }
            }
        })        
    }, [location])

     return (
            <div>{fileContents.map((file, index) => 
                    <Breadcrumb key= {index} content = {file} location = {location.pathname}/>
            )}</div>);
}
